mapper.select:
SELECT * FROM (
SELECT rownum r_num, a.* FROM (SELECT * FROM
ott_user
ORDER BY u_idx DESC) a
) WHERE r_num BETWEEN #{begin} AND
#{end}

SELECT * FROM ( SELECT ROWNUM AS R_NUM , A.* FROM (
SELECT * FROM OTT_USER
u_idx LIKE '%'||#{searchValue}||'%'
u_id LIKE '%'||#{searchValue}||'%'
u_name LIKE '%'||#{searchValue}||'%'
u_email LIKE '%'||#{searchValue}||'%'
is_stop = #{searchValue}
ORDER BY U_IDX DESC
) A ) WHERE R_NUM BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE OTT_USER
SET IS_STOP = #{is_stop}
WHERE U_IDX =
#{u_idx}
